(65, 'subscriptions', 'subscription-payment-succeeded', 'english', 'Subscription Payment Succeeded (Sent to customer primary contact)', 'Subscription  Payment Receipt - {subscription_name}', 'Hello&amp;nbsp;{contact_firstname}&amp;nbsp;{contact_lastname}&lt;br /&gt;&lt;br /&gt;This email is to let you know that we received your payment for subscription&amp;nbsp;&lt;strong&gt;{subscription_name}&amp;nbsp;&lt;/strong&gt;of&amp;nbsp;&lt;strong&gt;&lt;span&gt;{payment_total}&lt;br /&gt;&lt;br /&gt;&lt;/span&gt;&lt;/strong&gt;The invoice associated with it is now with status&amp;nbsp;&lt;strong&gt;{invoice_status}&lt;br /&gt;&lt;/strong&gt;&lt;br /&gt;Thank you for your confidence.&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(66, 'contract', 'contract-expiration-to-staff', 'english', 'Contract Expiration Reminder (Sent to Staff)', 'Contract Expiration Reminder', 'Hi {staff_firstname}&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;This is a reminder that the following contract will expire soon:&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Subject:&lt;/strong&gt; {contract_subject}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Description:&lt;/strong&gt; {contract_description}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Date Start:&lt;/strong&gt; {contract_datestart}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Date End:&lt;/strong&gt; {contract_dateend}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Kind Regards,&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(67, 'gdpr', 'gdpr-removal-request', 'english', 'Removal Request From Contact (Sent to administrators)', 'Data Removal Request Received', 'Hello&amp;nbsp;{staff_firstname}&amp;nbsp;{staff_lastname}&lt;br /&gt;&lt;br /&gt;Data removal has been requested by&amp;nbsp;{contact_firstname} {contact_lastname}&lt;br /&gt;&lt;br /&gt;You can review this request and take proper actions directly from the admin area.', '{companyname} | CRM', '', 0, 1, 0),
(68, 'gdpr', 'gdpr-removal-request-lead', 'english', 'Removal Request From Lead (Sent to administrators)', 'Data Removal Request Received', 'Hello&amp;nbsp;{staff_firstname}&amp;nbsp;{staff_lastname}&lt;br /&gt;&lt;br /&gt;Data removal has been requested by {lead_name}&lt;br /&gt;&lt;br /&gt;You can review this request and take proper actions directly from the admin area.&lt;br /&gt;&lt;br /&gt;To view the lead inside the admin area click here:&amp;nbsp;&lt;a href=\&quot;{lead_link}\&quot;&gt;{lead_link}&lt;/a&gt;', '{companyname} | CRM', '', 0, 1, 0),
(69, 'client', 'client-registration-confirmed', 'english', 'Customer Registration Confirmed', 'Your registration is confirmed', '&lt;p&gt;Dear {contact_firstname} {contact_lastname}&lt;br /&gt;&lt;br /&gt;We just wanted to let you know that your registration at&amp;nbsp;{companyname} is successfully confirmed and your account is now active.&lt;br /&gt;&lt;br /&gt;You can login at&amp;nbsp;&lt;a href=\&quot;{crm_url}\&quot;&gt;{crm_url}&lt;/a&gt; with the email and password you provided during registration.&lt;br /&gt;&lt;br /&gt;Please contact us if you need any help.&lt;br /&gt;&lt;br /&gt;Kind Regards, &lt;br /&gt;{email_signature}&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;(This is an automated email, so please don\'t reply to this email address)&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0),
