(51, 'ticket', 'ticket-assigned-to-admin', 'english', 'New Ticket Assigned (Sent to Staff)', 'New support ticket has been assigned to you', '&lt;p&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Hi&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;A new support ticket&amp;nbsp;has been assigned to you.&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Subject&lt;/strong&gt;: {ticket_subject}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Department&lt;/strong&gt;: {ticket_department}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Priority&lt;/strong&gt;: {ticket_priority}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Ticket message:&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{ticket_message}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;You can view the ticket on the following link: &lt;a href=\&quot;{ticket_url}\&quot;&gt;#{ticket_id}&lt;/a&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Kind Regards,&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0),
(52, 'client', 'new-client-registered-to-admin', 'english', 'New Customer Registration (Sent to admins)', 'New Customer Registration', 'Hello.&lt;br /&gt;&lt;br /&gt;New customer registration on your customer portal:&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Firstname:&lt;/strong&gt;&amp;nbsp;{contact_firstname}&lt;br /&gt;&lt;strong&gt;Lastname:&lt;/strong&gt;&amp;nbsp;{contact_lastname}&lt;br /&gt;&lt;strong&gt;Company:&lt;/strong&gt;&amp;nbsp;{client_company}&lt;br /&gt;&lt;strong&gt;Email:&lt;/strong&gt;&amp;nbsp;{contact_email}&lt;br /&gt;&lt;br /&gt;Best Regards', '{companyname} | CRM', '', 0, 1, 0),
(53, 'leads', 'new-web-to-lead-form-submitted', 'english', 'Web to lead form submitted - Sent to lead', '{lead_name} - We Received Your Request', 'Hello {lead_name}.&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Your request has been received.&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;This email is to let you know that we received your request and we will get back to you as soon as possible with more information.&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 0, 0),
(54, 'staff', 'two-factor-authentication', 'english', 'Two Factor Authentication', 'Confirm Your Login', '&lt;p&gt;Hi {staff_firstname}&lt;/p&gt;\r\n&lt;p style=\&quot;text-align: left;\&quot;&gt;You received this email because you have enabled two factor authentication in your account.&lt;br /&gt;Use the following code to confirm your login:&lt;/p&gt;\r\n&lt;p style=\&quot;text-align: left;\&quot;&gt;&lt;span style=\&quot;font-size: 18pt;\&quot;&gt;&lt;strong&gt;{two_factor_auth_code}&lt;br /&gt;&lt;br /&gt;&lt;/strong&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;&lt;strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0),
(55, 'project', 'project-finished-to-customer', 'english', 'Project Marked as Finished (Sent to Customer Contacts)', 'Project Marked as Finished', '&lt;p&gt;Hello&amp;nbsp;{contact_firstname}&amp;nbsp;{contact_lastname}&lt;/p&gt;\r\n&lt;p&gt;You are receiving this email because project&amp;nbsp;&lt;strong&gt;{project_name}&lt;/strong&gt; has been marked as finished. This project is assigned under your company and we just wanted to keep you up to date.&lt;br /&gt;&lt;br /&gt;You can view the project on the following link:&amp;nbsp;&lt;a href=\&quot;{project_link}\&quot;&gt;{project_name}&lt;/a&gt;&lt;/p&gt;\r\n&lt;p&gt;If you have any questions don\'t hesitate to contact us.&lt;br /&gt;&lt;br /&gt;Kind Regards,&lt;br /&gt;{email_signature}&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0),
(56, 'credit_note', 'credit-note-send-to-client', 'english', 'Send Credit Note To Email', 'Credit Note With Number #{credit_note_number} Created', 'Dear&amp;nbsp;{contact_firstname}&amp;nbsp;{contact_lastname}&lt;br /&gt;&lt;br /&gt;We have attached the credit note with number &lt;strong&gt;#{credit_note_number} &lt;/strong&gt;for your reference.&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Date:&lt;/strong&gt;&amp;nbsp;{credit_note_date}&lt;br /&gt;&lt;strong&gt;Total Amount:&lt;/strong&gt;&amp;nbsp;{credit_note_total}&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Please contact us for more information.&lt;/span&gt;&lt;br /&gt; &lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Kind Regards,&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(57, 'tasks', 'task-status-change-to-staff', 'english', 'Task Status Changed (Sent to Staff)', 'Task Status Changed', '&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Hi {staff_firstname}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;{task_user_take_action}&lt;/strong&gt; marked task as &lt;strong&gt;{task_status}&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Name:&lt;/strong&gt; {task_name}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Due date:&lt;/strong&gt; {task_duedate}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;You can view the task on the following link: &lt;a href=\&quot;{task_link}\&quot;&gt;{task_name}&lt;/a&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Kind Regards,&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(58, 'tasks', 'task-status-change-to-contacts', 'english', 'Task Status Changed (Sent to Customer Contacts)', 'Task Status Changed', '&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Hi {contact_firstname} {contact_lastname}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;{task_user_take_action}&lt;/strong&gt; marked task as &lt;strong&gt;{task_status}&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Name:&lt;/strong&gt; {task_name}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Due date:&lt;/strong&gt; {task_duedate}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;You can view the task on the following link: &lt;a href=\&quot;{task_link}\&quot;&gt;{task_name}&lt;/a&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Kind Regards,&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(59, 'staff', 'reminder-email-staff', 'english', 'Staff Reminder Email', 'You Have a New Reminder!', '&lt;p&gt;Hello&amp;nbsp;{staff_firstname}&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;You have a new reminder&amp;nbsp;linked to&amp;nbsp;{staff_reminder_relation_name}!&lt;br /&gt;&lt;br /&gt;Reminder description:&lt;/strong&gt;&lt;br /&gt;{staff_reminder_description}&lt;br /&gt;&lt;br /&gt;Click &lt;a href=\&quot;{staff_reminder_relation_link}\&quot;&gt;here&lt;/a&gt; to view&amp;nbsp;&lt;a href=\&quot;{staff_reminder_relation_link}\&quot;&gt;{staff_reminder_relation_name}&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;Best Regards&lt;br /&gt;&lt;br /&gt;&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0),
(60, 'contract', 'contract-comment-to-client', 'english', 'New Comment (Sent to Customer Contacts)', 'New Contract Comment', 'Dear {contact_firstname} {contact_lastname}&lt;br /&gt; &lt;br /&gt;A new comment has been made on the following contract: &lt;strong&gt;{contract_subject}&lt;/strong&gt;&lt;br /&gt; &lt;br /&gt;You can view and reply to the comment on the following link: &lt;a href=\&quot;{contract_link}\&quot;&gt;{contract_subject}&lt;/a&gt;&lt;br /&gt; &lt;br /&gt;Kind Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(61, 'contract', 'contract-comment-to-admin', 'english', 'New Comment (Sent to Staff) ', 'New Contract Comment', 'Hi {staff_firstname}&lt;br /&gt;&lt;br /&gt;A new comment has been made to the contract&amp;nbsp;&lt;strong&gt;{contract_subject}&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;You can view and reply to the comment on the following link: &lt;a href=\&quot;{contract_link}\&quot;&gt;{contract_subject}&lt;/a&gt;&amp;nbsp;or from the admin area.&lt;br /&gt;&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(62, 'subscriptions', 'send-subscription', 'english', 'Send Subscription to Customer', 'Subscription Created', 'Hello&amp;nbsp;{contact_firstname}&amp;nbsp;{contact_lastname}&lt;br /&gt;&lt;br /&gt;We have prepared the subscription&amp;nbsp;&lt;strong&gt;{subscription_name}&lt;/strong&gt; for your company.&lt;br /&gt;&lt;br /&gt;Click &lt;a href=\&quot;{subscription_link}\&quot;&gt;here&lt;/a&gt; to review the subscription and subscribe.&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(63, 'subscriptions', 'subscription-payment-failed', 'english', 'Subscription Payment Failed', 'Your most recent invoice payment failed', 'Hello&amp;nbsp;{contact_firstname}&amp;nbsp;{contact_lastname}&lt;br /&gt;&lt;br br=\&quot;\&quot; /&gt;Unfortunately, your most recent invoice payment for&amp;nbsp;&lt;strong&gt;{subscription_name}&lt;/strong&gt; was declined.&lt;br /&gt;&lt;br /&gt;This could be due to a change in your card number, your card expiring,&lt;br /&gt;cancellation of your credit card, or the card issuer not recognizing the&lt;br /&gt;payment and therefore taking action to prevent it.&lt;br /&gt;&lt;br /&gt;Please update your payment information as soon as possible by logging in here:&lt;br /&gt;&lt;a href=\&quot;{crm_url}/login\&quot;&gt;{crm_url}/login&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(64, 'subscriptions', 'subscription-canceled', 'english', 'Subscription Canceled (Sent to customer primary contact)', 'Your subscription has been canceled', 'Hello&amp;nbsp;{contact_firstname}&amp;nbsp;{contact_lastname}&lt;br /&gt;&lt;br /&gt;Your subscription&amp;nbsp;&lt;strong&gt;{subscription_name} &lt;/strong&gt;has been canceled, if you have any questions don\'t hesitate to contact us.&lt;br /&gt;&lt;br /&gt;It was a pleasure doing business with you.&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(65, 'subscriptions', 'subscription-payment-succeeded', 'english', 'Subscription Payment Succeeded (Sent to customer primary contact)', 'Subscription  Payment Receipt - {subscription_name}', 'Hello&amp;nbsp;{contact_firstname}&amp;nbsp;{contact_lastname}&lt;br /&gt;&lt;br /&gt;This email is to let you know that we received your payment for subscription&amp;nbsp;&lt;strong&gt;{subscription_name}&amp;nbsp;&lt;/strong&gt;of&amp;nbsp;&lt;strong&gt;&lt;span&gt;{payment_total}&lt;br /&gt;&lt;br /&gt;&lt;/span&gt;&lt;/strong&gt;The invoice associated with it is now with status&amp;nbsp;&lt;strong&gt;{invoice_status}&lt;br /&gt;&lt;/strong&gt;&lt;br /&gt;Thank you for your confidence.&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(66, 'contract', 'contract-expiration-to-staff', 'english', 'Contract Expiration Reminder (Sent to Staff)', 'Contract Expiration Reminder', 'Hi {staff_firstname}&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;This is a reminder that the following contract will expire soon:&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Subject:&lt;/strong&gt; {contract_subject}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Description:&lt;/strong&gt; {contract_description}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Date Start:&lt;/strong&gt; {contract_datestart}&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;Date End:&lt;/strong&gt; {contract_dateend}&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Kind Regards,&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(67, 'gdpr', 'gdpr-removal-request', 'english', 'Removal Request From Contact (Sent to administrators)', 'Data Removal Request Received', 'Hello&amp;nbsp;{staff_firstname}&amp;nbsp;{staff_lastname}&lt;br /&gt;&lt;br /&gt;Data removal has been requested by&amp;nbsp;{contact_firstname} {contact_lastname}&lt;br /&gt;&lt;br /&gt;You can review this request and take proper actions directly from the admin area.', '{companyname} | CRM', '', 0, 1, 0),
(68, 'gdpr', 'gdpr-removal-request-lead', 'english', 'Removal Request From Lead (Sent to administrators)', 'Data Removal Request Received', 'Hello&amp;nbsp;{staff_firstname}&amp;nbsp;{staff_lastname}&lt;br /&gt;&lt;br /&gt;Data removal has been requested by {lead_name}&lt;br /&gt;&lt;br /&gt;You can review this request and take proper actions directly from the admin area.&lt;br /&gt;&lt;br /&gt;To view the lead inside the admin area click here:&amp;nbsp;&lt;a href=\&quot;{lead_link}\&quot;&gt;{lead_link}&lt;/a&gt;', '{companyname} | CRM', '', 0, 1, 0),
(69, 'client', 'client-registration-confirmed', 'english', 'Customer Registration Confirmed', 'Your registration is confirmed', '&lt;p&gt;Dear {contact_firstname} {contact_lastname}&lt;br /&gt;&lt;br /&gt;We just wanted to let you know that your registration at&amp;nbsp;{companyname} is successfully confirmed and your account is now active.&lt;br /&gt;&lt;br /&gt;You can login at&amp;nbsp;&lt;a href=\&quot;{crm_url}\&quot;&gt;{crm_url}&lt;/a&gt; with the email and password you provided during registration.&lt;br /&gt;&lt;br /&gt;Please contact us if you need any help.&lt;br /&gt;&lt;br /&gt;Kind Regards, &lt;br /&gt;{email_signature}&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;(This is an automated email, so please don\'t reply to this email address)&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0),
(70, 'contract', 'contract-signed-to-staff', 'english', 'Contract Signed (Sent to Staff)', 'Customer Signed a Contract', 'Hi {staff_firstname}&lt;br /&gt;&lt;br /&gt;A contract with subject&amp;nbsp;&lt;strong&gt;{contract_subject} &lt;/strong&gt;has been successfully signed by the customer.&lt;br /&gt;&lt;br /&gt;You can view the contract at the following link: &lt;a href=\&quot;{contract_link}\&quot;&gt;{contract_subject}&lt;/a&gt;&amp;nbsp;or from the admin area.&lt;br /&gt;&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(71, 'subscriptions', 'customer-subscribed-to-staff', 'english', 'Customer Subscribed to a Subscription (Sent to administrators and subscription creator)', 'Customer Subscribed to a Subscription', 'The customer &lt;strong&gt;{client_company}&lt;/strong&gt; subscribed to a subscription with name&amp;nbsp;&lt;strong&gt;{subscription_name}&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;ID&lt;/strong&gt;:&amp;nbsp;{subscription_id}&lt;br /&gt;&lt;strong&gt;Subscription name&lt;/strong&gt;:&amp;nbsp;{subscription_name}&lt;br /&gt;&lt;strong&gt;Subscription description&lt;/strong&gt;:&amp;nbsp;{subscription_description}&lt;br /&gt;&lt;br /&gt;You can view the subscription by clicking &lt;a href=\&quot;{subscription_link}\&quot;&gt;here&lt;/a&gt;&lt;br /&gt;\r\n&lt;div style=\&quot;text-align: center;\&quot;&gt;&lt;span style=\&quot;font-size: 10pt;\&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/div&gt;\r\nBest Regards,&lt;br /&gt;{email_signature}&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 10pt;\&quot;&gt;&lt;span style=\&quot;color: #999999;\&quot;&gt;You are receiving this email because you are either administrator or you are creator of the subscription.&lt;/span&gt;&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(72, 'client', 'contact-verification-email', 'english', 'Email Verification (Sent to Contact After Registration)', 'Verify Email Address', '&lt;p&gt;Hello&amp;nbsp;{contact_firstname}&lt;br /&gt;&lt;br /&gt;Please click the button below to verify your email address.&lt;br /&gt;&lt;br /&gt;&lt;a href=\&quot;{email_verification_url}\&quot;&gt;Verify Email Address&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;If you did not create an account, no further action is required&lt;/p&gt;\r\n&lt;p&gt;&lt;br /&gt;{email_signature}&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0),
(73, 'client', 'new-customer-profile-file-uploaded-to-staff', 'english', 'New Customer Profile File(s) Uploaded (Sent to Staff)', 'Customer Uploaded New File(s) in Profile', 'Hi!&lt;br /&gt;&lt;br /&gt;New file(s) is uploaded into the customer ({client_company}) profile by&amp;nbsp;{contact_firstname}&lt;br /&gt;&lt;br /&gt;You can check the uploaded files into the admin area by clicking &lt;a href=\&quot;{customer_profile_files_admin_link}\&quot;&gt;here&lt;/a&gt; or at the following link:&amp;nbsp;{customer_profile_files_admin_link}&lt;br /&gt;&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(74, 'staff', 'event-notification-to-staff', 'english', 'Event Notification (Calendar)', 'Upcoming Event - {event_title}', 'Hi {staff_firstname}! &lt;br /&gt;&lt;br /&gt;This is a reminder for event &lt;a href=\\\&quot;{event_link}\\\&quot;&gt;{event_title}&lt;/a&gt; scheduled at {event_start_date}. &lt;br /&gt;&lt;br /&gt;Regards.', '', '', 0, 1, 0),
(75, 'subscriptions', 'subscription-payment-requires-action', 'english', 'Credit Card Authorization Required - SCA', 'Important: Confirm your subscription {subscription_name} payment', '&lt;p&gt;Hello {contact_firstname}&lt;/p&gt;\r\n&lt;p&gt;&lt;strong&gt;Your bank sometimes requires an additional step to make sure an online transaction was authorized.&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;Because of European regulation to protect consumers, many online payments now require two-factor authentication. Your bank ultimately decides when authentication is required to confirm a payment, but you may notice this step when you start paying for a service or when the cost changes.&lt;br /&gt;&lt;br /&gt;In order to pay the subscription &lt;strong&gt;{subscription_name}&lt;/strong&gt;, you will need to&amp;nbsp;confirm your payment by clicking on the follow link: &lt;strong&gt;&lt;a href=\&quot;{subscription_authorize_payment_link}\&quot;&gt;{subscription_authorize_payment_link}&lt;/a&gt;&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;To view the subscription, please click at the following link: &lt;a href=\&quot;{subscription_link}\&quot;&gt;&lt;span&gt;{subscription_link}&lt;/span&gt;&lt;/a&gt;&lt;br /&gt;or you can login in our dedicated area here: &lt;a href=\&quot;{crm_url}/login\&quot;&gt;{crm_url}/login&lt;/a&gt; in case you want to update your credit card or view the subscriptions you are subscribed.&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0);
INSERT INTO `tblemailtemplates` (`emailtemplateid`, `type`, `slug`, `language`, `name`, `subject`, `message`, `fromname`, `fromemail`, `plaintext`, `active`, `order`) VALUES
(76, 'invoice', 'invoice-due-notice', 'english', 'Invoice Due Notice', 'Your {invoice_number} will be due soon', '&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Hi {contact_firstname} {contact_lastname}&lt;br /&gt;&lt;br /&gt;&lt;/span&gt;You invoice &lt;span style=\&quot;font-size: 12pt;\&quot;&gt;&lt;strong&gt;# {invoice_number} &lt;/strong&gt;will be due on &lt;strong&gt;{invoice_duedate}&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;You can view the invoice on the following link: &lt;a href=\&quot;{invoice_link}\&quot;&gt;{invoice_number}&lt;/a&gt;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;Kind Regards,&lt;/span&gt;&lt;br /&gt;&lt;span style=\&quot;font-size: 12pt;\&quot;&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(77, 'estimate_request', 'estimate-request-submitted-to-staff', 'english', 'Estimate Request Submitted (Sent to Staff)', 'New Estimate Request Submitted', '&lt;span&gt; Hello,&amp;nbsp;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;{estimate_request_email} submitted an estimate request via the {estimate_request_form_name} form.&lt;br /&gt;&lt;br /&gt;You can view the request at the following link: &lt;a href=\&quot;{estimate_request_link}\&quot;&gt;{estimate_request_link}&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;==&lt;br /&gt;&lt;br /&gt;{estimate_request_submitted_data}&lt;br /&gt;&lt;br /&gt;Kind Regards,&lt;br /&gt;&lt;span&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(78, 'estimate_request', 'estimate-request-assigned', 'english', 'Estimate Request Assigned (Sent to Staff)', 'New Estimate Request Assigned', '&lt;span&gt; Hello {estimate_request_assigned},&amp;nbsp;&lt;/span&gt;&lt;br /&gt;&lt;br /&gt;Estimate request #{estimate_request_id} has been assigned to you.&lt;br /&gt;&lt;br /&gt;You can view the request at the following link: &lt;a href=\&quot;{estimate_request_link}\&quot;&gt;{estimate_request_link}&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;Kind Regards,&lt;br /&gt;&lt;span&gt;{email_signature}&lt;/span&gt;', '{companyname} | CRM', '', 0, 1, 0),
(79, 'estimate_request', 'estimate-request-received-to-user', 'english', 'Estimate Request Received (Sent to User)', 'Estimate Request Received', 'Hello,&lt;br /&gt;&lt;br /&gt;&lt;strong&gt;Your request has been received.&lt;/strong&gt;&lt;br /&gt;&lt;br /&gt;This email is to let you know that we received your request and we will get back to you as soon as possible with more information.&lt;br /&gt;&lt;br /&gt;Best Regards,&lt;br /&gt;{email_signature}', '{companyname} | CRM', '', 0, 0, 0),
(80, 'notifications', 'non-billed-tasks-reminder', 'english', 'Non-billed tasks reminder (sent to selected staff members)', 'Action required: Completed tasks are not billed', 'Hello {staff_firstname}&lt;br&gt;&lt;br&gt;The following tasks are marked as complete but not yet billed:&lt;br&gt;&lt;br&gt;{unbilled_tasks_list}&lt;br&gt;&lt;br&gt;Kind Regards,&lt;br&gt;&lt;br&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(81, 'invoice', 'invoices-batch-payments', 'english', 'Invoices Payments Recorded in Batch (Sent to Customer)', 'We have received your payments', 'Hello {contact_firstname} {contact_lastname}&lt;br&gt;&lt;br&gt;Thank you for the payments. Please find the payments details below:&lt;br&gt;&lt;br&gt;{batch_payments_list}&lt;br&gt;&lt;br&gt;We are looking forward working with you.&lt;br&gt;&lt;br&gt;Kind Regards,&lt;br&gt;&lt;br&gt;{email_signature}', '{companyname} | CRM', '', 0, 1, 0),
(82, 'contract', 'contract-sign-reminder', 'english', 'Contract Sign Reminder (Sent to Customer)', 'Contract Sign Reminder', '&lt;p&gt;Hello {contact_firstname} {contact_lastname}&lt;br /&gt;&lt;br /&gt;This is a reminder to review and sign the contract:&lt;a href=\&quot;{contract_link}\&quot;&gt;{contract_subject}&lt;/a&gt;&lt;/p&gt;&lt;p&gt;You can view and sign by visiting: &lt;a href=\&quot;{contract_link}\&quot;&gt;{contract_subject}&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;br /&gt;We are looking forward working with you.&lt;br /&gt;&lt;br /&gt;Kind Regards,&lt;br /&gt;&lt;br /&gt;{email_signature}&lt;/p&gt;', '{companyname} | CRM', '', 0, 1, 0);

